mapper.update:
update fitroot
set pw = #{pw}, birthday =
TO_DATE(#{birthday}, 'YYYY-MM-DD'), tel = #{tel}
where u_Idx = #{uIdx}

UPDATE fitroot
SET water = #{water}
WHERE u_Idx = #{uIdx}

UPDATE fitroot
SET startsleep = TO_TIMESTAMP(#{startsleep}, 'HH24:MI'),
endsleep = TO_TIMESTAMP(#{endsleep}, 'HH24:MI'),
sleepcheck = #{sleepcheck}
WHERE u_idx = #{uIdx}
